import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteMovie } from "../features/add/movieSlice";

const MovieControls = () => {
  const watchlist = useSelector((state) => state.movieReducer.watchlist);

  const dispatch = useDispatch();

  const deleteMovieFromML = (obj) => {
    dispatch(deleteMovie(obj));
  };

  return (
    <div className="inner-card-controls">
      {watchlist && (
        <>
          <button className="ctrl-btn">
            <i className="fa-fw fa fa-eye"></i>
          </button>
          <button className="ctrl-btn">
            <i className="fa-fw fa fa-times" onClick={() => watchlist}></i>
          </button>
        </>
      )}
    </div>
  );
};

export default MovieControls;
