import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  watchlist: JSON.parse(localStorage.getItem("watchlist")) || [],
  watched: [],
};

export const movieSlice = createSlice({
  name: "movieSlice",
  initialState,
  reducers: {
    addMovieToWatchList: (state, action) => {
      if (!state.watchlist.some((obj) => obj.id === action.payload.id)) {
        state.watchlist.unshift(action.payload);
      }
    },

    addMovieToWatched: (state, action) => {
      state.watched.unshift(action.payload);
    },

    deleteMovie: (state, action) => {
      state.watchlist.map((obj) => obj.id !== action.payload);
    },
  },
});

// Action creators are generated for each case reducer function
export const { addMovieToWatchList, addMovieToWatched, deleteMovie } =
  movieSlice.actions;

export default movieSlice.reducer;
