import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteMovie } from "../features/add/movieSlice";

const MovieControls = ({ id }) => {
  const watchlist = useSelector((state) => state.movieReducer.watchlist);

  const dispatch = useDispatch();

  const obj = [{ key: 123 }, { key: 456 }, { key: 789 }];
  let change;

  const changeFn = (myId) => {
    let change = obj.filter((obj) => obj.id !== myId);
    console.log("ðŸš€ ~ file: MovieControls.js:14 ~ changeFn ~ change", change);
  };
  changeFn(123);

  // const onClickRemove = () => {
  //   dispatch(deleteMovie(id));
  //   console.log(id);
  // };

  return (
    <div className="inner-card-controls">
      {watchlist && (
        <>
          <button className="ctrl-btn">
            <i className="fa-fw fa fa-eye"></i>
          </button>
          <button className="ctrl-btn">
            <i
              className="fa-fw fa fa-times"
              onClick={() => dispatch(deleteMovie(id))}
            ></i>
          </button>
        </>
      )}
    </div>
  );
};

export default MovieControls;
